BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS `users` (
	`id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	`username`	TEXT DEFAULT NULL,
	`password`	TEXT DEFAULT NULL,
	`figure`	TEXT NOT NULL,
	`sex`	TEXT NOT NULL DEFAULT 'M',
	`motto`	TEXT NOT NULL DEFAULT 'de kepler whey',
	`credits`	int ( 11 ) NOT NULL DEFAULT '200',
	`tickets`	int ( 11 ) NOT NULL DEFAULT '0',
	`film`	int ( 11 ) NOT NULL DEFAULT '0'
);
CREATE TABLE IF NOT EXISTS `rooms_models` (
	`modeltype`	TEXT NOT NULL,
	`door_x`	tinyint ( 3 ) NOT NULL DEFAULT '0',
	`door_y`	tinyint ( 3 ) NOT NULL DEFAULT '0',
	`door_z`	float NOT NULL,
	`heightmap`	text NOT NULL,
	`usertype`	INT NOT NULL DEFAULT '1'
);
CREATE TABLE IF NOT EXISTS `rooms_categories` (
	`id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	`order_id`	int ( 11 ) NOT NULL,
	`parent_id`	int ( 4 ) NOT NULL,
	`isnode`	INT NOT NULL DEFAULT '0',
	`name`	TEXT NOT NULL,
	`public_spaces`	INT ( 1 ) NOT NULL DEFAULT '0',
	`allow_trading`	INT ( 1 ) NOT NULL DEFAULT '0',
	`minrole_access`	int ( 1 ) NOT NULL DEFAULT '1',
	`minrole_setflatcat`	int ( 1 ) NOT NULL DEFAULT '1'
);
CREATE TABLE IF NOT EXISTS `rooms` (
	`id`	int ( 11 ) NOT NULL,
	`owner_id`	int ( 11 ) NOT NULL,
	`category`	int ( 3 ) NOT NULL DEFAULT '0',
	`name`	TEXT NOT NULL,
	`description`	TEXT NOT NULL,
	`model`	TEXT NOT NULL,
	`ccts`	TEXT DEFAULT NULL,
	`wallpaper`	int ( 4 ) NOT NULL DEFAULT '0',
	`floor`	int ( 4 ) NOT NULL DEFAULT '0',
	`showname`	INT ( 1 ) NOT NULL DEFAULT 1,
	`superusers`	INT ( 1 ) NOT NULL DEFAULT 0,
	`accesstype`	INT ( 0 ) NOT NULL DEFAULT 0,
	`password`	TEXT NOT NULL,
	`visitors_now`	int ( 3 ) NOT NULL DEFAULT '0',
	`visitors_max`	int ( 3 ) NOT NULL DEFAULT '25'
);
COMMIT;
